(863, 4, 86, 'javascript 的 typeof 返回哪些数据类型', '', '/upload/201708/28/time.jpg', 'number、boolean、string 、underfind、object、functionconsole.log(typeof function);console.log(typeof string);console.log(typeof undefined);console.log(typeof null);//会报错,这是个关键字//&quot;undefined&quot;//&quot;undefined&quot;//object,比较特殊,要…', '<pre class="brush:js;toolbar:false">number、boolean、string&nbsp;、underfind、object、function\r\nconsole.log(typeof&nbsp;function);\r\nconsole.log(typeof&nbsp;string);\r\nconsole.log(typeof&nbsp;undefined);\r\nconsole.log(typeof&nbsp;null);\r\n//会报错,这是个关键字\r\n//&quot;undefined&quot;\r\n//&quot;undefined&quot;\r\n//object,比较特殊,要记住\r\n//function,是个判断函数\r\n//boolean,结果是布尔型\r\n//object,数组是对象\r\n//false,返回是布尔值\r\nconsole.log(typeof&nbsp;isNaN);\r\nconsole.log(typeof&nbsp;isNaN(123));\r\nconsole.log(typeof&nbsp;[]);\r\nconsole.log(Array.isArray());\r\n26\r\n\r\n&nbsp;\r\n\r\nconsole.log(Array.isArray);\r\nconsole.log(toString.call([]));\r\nconsole.log([].constructor);\r\n//function&nbsp;isArray()&nbsp;{&nbsp;[native&nbsp;code]&nbsp;}\r\n//&quot;[object&nbsp;Array]&quot;\r\n//function&nbsp;Array()&nbsp;{&nbsp;[native&nbsp;code]&nbsp;}</pre>', 100, 0, 0, 1, 1, 1, 0, 'admin', '2017-08-28 00:00:00', '2017-08-29 19:46:06', '管理员', ''),
